export const sampleBreeds = breeds;
export const sampleArticles = articles;

export function getAllBreeds() {
  return breeds;
}

export function getBreedBySlug(slug: string) {
  return breeds.find(breed => breed.slug === slug);
}

export function getTopBreeds(count: number = 6) {
  return breeds.slice(0, count);
}

export function getAllArticles() {
  // Use content-lists.ts which is updated automatically by auto-post
  const { allArticles } = require('./content-lists');
  // Convert ArticleInfo to Article format with default values
  return allArticles.map((article: any) => ({
    ...article,
    description: article.title, // Use title as description if not provided
    image: `/images/articles/${article.slug}.jpg`,
    readingTime: 5,
    date: new Date().toISOString().split('T')[0],
    author: 'Dr. Veterinar Pisicopedia',
  }));
}

export function getArticleBySlug(slug: string) {
  const { allArticles } = require('./content-lists');
  const article = allArticles.find((a: any) => a.slug === slug);
  if (!article) return undefined;
  // Convert ArticleInfo to Article format with default values
  return {
    ...article,
    description: article.title,
    image: `/images/articles/${article.slug}.jpg`,
    readingTime: 5,
    date: new Date().toISOString().split('T')[0],
    author: 'Dr. Veterinar Pisicopedia',
  };
}

export function getLatestArticles(count: number = 6) {
  const { allArticles } = require('./content-lists');
  return allArticles.slice(0, count).map((article: any) => ({
    ...article,
    description: article.title,
    image: `/images/articles/${article.slug}.jpg`,
    readingTime: 5,
    date: new Date().toISOString().split('T')[0],
    author: 'Dr. Veterinar Pisicopedia',
  }));
}

export function getArticlesByCategory(category: string) {
  const { allArticles } = require('./content-lists');
  return allArticles
    .filter((article: any) => article.category === category)
    .map((article: any) => ({
      ...article,
      description: article.title,
      image: `/images/articles/${article.slug}.jpg`,
      readingTime: 5,
      date: new Date().toISOString().split('T')[0],
      author: 'Dr. Veterinar Pisicopedia',
    }));
}

export function getAllGuides() {
  // Use content-lists.ts which has all 24 guides
  const { allGuides } = require('./content-lists');
  return allGuides.map((guide: any) => ({
    ...guide,
    description: guide.title,
    image: `/images/guides/${guide.slug}.jpg`,
    readingTime: 10,
  }));
}

export function getGuideBySlug(slug: string) {
  const { allGuides } = require('./content-lists');
  const guide = allGuides.find((g: any) => g.slug === slug);
  if (!guide) return undefined;
  return {
    ...guide,
    description: guide.title,
    image: `/images/guides/${guide.slug}.jpg`,
    readingTime: 10,
  };
}

export function getTopGuides(count: number = 3) {
  const allGuides = getAllGuides();
  return allGuides.slice(0, count);
}

export function getGuidesByCategory(category: string) {
  const allGuides = getAllGuides();
  return allGuides.filter((guide: any) => guide.category === category);
}